var events = require("events");
var wsapEmitter = new events.EventEmitter();//创建了事件监听器的一个对象
function wsapChangeStatus(varResult)
{
	process.nextTick(function() {
        wsapEmitter.emit('changeState', varResult);
    }.bind(this));
}

exports.changeStatus = wsapChangeStatus;
exports.wsapEmitter = wsapEmitter;